<template>
  <div class="background-image">
    <div class="login-container">
      <div class="login">
        <h2>Login</h2>
        <form @submit.prevent="login">
          <div class="form-group">
            <label for="username">E-mail</label>
            <input type="text" id="username" v-model="email" required>
          </div>
          <div class="form-group">
            <label for="password">Password</label>
            <input type="password" id="password" v-model="password" required>
          </div>
          <button type="submit">Login</button>
        </form>
      </div>

      <footer class="footer">
        <p class="rights">&copy; 2024 Your Company. All rights reserved.</p>
      </footer>
    </div>
  </div>
</template>


  
  <script>
    import {auth} from '../Firebase/config.js'
    import { getUser,setUser } from '../Firebase/userstate.js';

  export default {
    data() {
      return {
        email: '',
        password: ''
      };
    },
    methods: {
      created() {
        auth.onAuthStateChanged((user) => {
          if (user) {
            setUser(user);
            console.log(`User ID: ${user.uid}`);
          } else {
            setUser(null);
            console.log("User is signed out");
          }
        })
},
      async login() {
        try {
            const userCredential=await auth.signInWithEmailAndPassword(this.email, this.password);
            this.user = userCredential.user;
            console.log(`User ID: ${this.user.uid}`);
            setUser(this.user);
           
            this.$router.push('/home'); 
        } catch (error) {
            alert(`Login failed: ${error.message}`);
        }
}

    }
  }
  </script>
  
